var modal=document.querySelector(".modal"),button=document.querySelector(".search-form-button"),form=document.querySelector(".search-form"),checkinDate=document.querySelector(".check-in-date"),checkoutDate=document.querySelector(".check-out-date"),adults=document.querySelector(".number-of-adults"),children=document.querySelector(".number-of-children");button.addEventListener("click",function(e){e.preventDefault(),modal.classList.toggle("modal-hide"),modal.classList.toggle("modal-show")}),form.addEventListener("submit",function(e){checkinDate.value&&checkoutDate.value&&adults.value&&children.value||(e.preventDefault(),modal.classList.add("modal-error"));setTimeout(function(){modal.classList.remove("modal-error")},500)});